body.script:
/*  */
  // GitHub PagesのURL
  const baseUrl = "https://0xfab0131.github.io/0xfab0131";

  // アセットURLを生成する関数
  function getAssetUrl(key) {
    return `${baseUrl}?asset=${key}`;
  }

  // 画像要素の属性を設定
  window.onload = function() {
    // プロフィールビューと GitHub フォロワー
    document.getElementById('profile-view-img').src = getAssetUrl('profileViews');
    document.getElementById('github-followers-img').src = getAssetUrl('githubFollowers');

    // ウェルカム GIF
    document.getElementById('welcome-gif').src = getAssetUrl('welcomeGif');

    // フロントエンドバッジ
    document.getElementById('react-img').src = getAssetUrl('react');
    document.getElementById('nextjs-img').src = getAssetUrl('nextjs');
    document.getElementById('typescript-img').src = getAssetUrl('typescript');
    document.getElementById('javascript-img').src = getAssetUrl('javascript');
    document.getElementById('threejs-img').src = getAssetUrl('threejs');
    document.getElementById('tailwind-img').src = getAssetUrl('tailwind');
    document.getElementById('materialui-img').src = getAssetUrl('materialui');
    document.getElementById('astro-img').src = getAssetUrl('astro');

    // バックエンドバッジ
    document.getElementById('nodejs-img').src = getAssetUrl('nodejs');
    document.getElementById('go-img').src = getAssetUrl('go');
    document.getElementById('python-img').src = getAssetUrl('python');
    document.getElementById('csharp-img').src = getAssetUrl('csharp');
    document.getElementById('dotnet-img').src = getAssetUrl('dotnet');
    document.getElementById('docker-img').src = getAssetUrl('docker');
    document.getElementById('kubernetes-img').src = getAssetUrl('kubernetes');
    document.getElementById('graphql-img').src = getAssetUrl('graphql');
    document.getElementById('postgresql-img').src = getAssetUrl('postgresql');

    // クラウド and DevOpsバッジ
    document.getElementById('aws-img').src = getAssetUrl('aws');
    document.getElementById('azure-img').src = getAssetUrl('azure');
    document.getElementById('gcp-img').src = getAssetUrl('gcp');
    document.getElementById('github-actions-img').src = getAssetUrl('githubActions');
    document.getElementById('vercel-img').src = getAssetUrl('vercel');
    document.getElementById('firebase-img').src = getAssetUrl('firebase');
    document.getElementById('supabase-img').src = getAssetUrl('supabase');
    document.getElementById('cloudflare-img').src = getAssetUrl('cloudflare');

    // GitHub統計
    document.getElementById('github-stats-img').src = getAssetUrl('githubStats');
    document.getElementById('top-langs-img').src = getAssetUrl('topLanguages');
    document.getElementById('streak-stats-img').src = getAssetUrl('streakStats');

    // SNSリンク
    document.getElementById('twitter-img').src = getAssetUrl('twitter');
    document.getElementById('github-img').src = getAssetUrl('github');
  };
/*  */
